import { useState } from "react";

export default function BoardUpdate(props){
    //상세보기 , 배열
    let {게시글배열, 게시글배열변경함수, 상세보기} = props;

    let [입력값, 입력값변경] = useState (상세보기);

    function 게시글수정(){

    }
    function onInputHandler(e){
        let {name , value} = e.target
        입력값변경({...입력값, [name] : value});
      }
    return (
        <div className='outer'>
        <br />
        <h2>수정</h2>
        <table className='enroll-table'>
          <tr>
            <th>제목</th>
            <td colSpan={3}>
              <input type='text' name='글제목'
              onChange={function(e){
                입력값변경({...입력값, 글제목 : e.target.value});
              }}
              value={입력값.글제목}
              />
            </td>
          </tr>
          <tr>
            <th>작성자</th>
            <td colSpan={3}>
              <input type='text' name='작성자'
              onChange={function(e){
                console.dir(e.target)
                입력값변경({
                  ...입력값 ,
                  [e.target.name] : e.target.value
                })
                // 대괄호 사용이유
                // let name = "글제목";
                //{name : e.target.value} => {name: 'zzz'}
                // { [name] : e,target.value} => {글제목 : 'ㅋㅋㅋ'}
              }}
              value={입력값.작성자}
              />
            </td>
          </tr>
          <tr>
            <th>글내용</th>
            <td colSpan={3} style={ { height: "200px" }  }>
              <textarea name='글내용'
              onChange={onInputHandler}
              value={입력값.글내용}
              ></textarea>
            </td>
          </tr>
          <tr>
            <th colSpan={4}><button onClick={ () => 게시글수정() }>등록</button></th>
          </tr>
        </table>
    </div>
  )
}
