import { useState } from "react";

export default function BoardUpdate(props){
    //상세보기 , 배열
    let {게시글배열, 게시글배열변경함수, 상세보기, 레이아웃변경} = props;

    let [입력값, 입력값변경] = useState (상세보기);


    function 게시글수정(수정할글번호){
        // 수정 완료 후 내가 작성한 게시글 상세보기 페이지로 이동
        
        let 필터링배열 = 게시글배열.filter( function(게시글) {
            return 게시글.글번호 !== 수정할글번호
        });
        게시글배열변경함수(필터링배열);
    }


    function onInputHandler(e){
        let {name , value} = e.target
        입력값변경({...입력값, [name] : value});
      }
    return (
        <div className='outer'>
        <br />
        <h2>수정</h2>
        <table className='enroll-table'>
          <tr>
            <th>제목</th>
            <td colSpan={3}>
              <input type='text' name='글제목'
              onChange={onInputHandler}
              value={입력값.글제목}
              />
            </td>
          </tr>
          <tr>
            <th>작성자</th>
            <td colSpan={3}>
              <input type='text' name='작성자'
              onChange={{onInputHandler}
                // 대괄호 사용이유
                // let name = "글제목";
                //{name : e.target.value} => {name: 'zzz'}
                // { [name] : e,target.value} => {글제목 : 'ㅋㅋㅋ'}
    }
              value={입력값.작성자}
              />
            </td>
          </tr>
          <tr>
            <th>글내용</th>
            <td colSpan={3} style={ { height: "200px" }  }>
              <textarea name='글내용'
              onChange={onInputHandler}
              value={입력값.글내용}
              ></textarea>
            </td>
          </tr>
          <tr>
          <td><button onClick={(e) => {
                    e.stopPropagation();
                    게시글수정(게시글.글번호)}}>삭제</button></td>
                </tr>
        </table>
    </div>
  )
}
