import './App.css';
import { useState } from 'react';
import data from './data'; // default 옵션으로 export한 경우 변수명을 다르게 가져올 수 있음.

function App() { //jsx 문법 => 자바스크립트 안에 html 사용가능.

  let 제목 = "KH D CLASS";

  //state 문법
  let [제목2, 제목변경함수] = useState('KH E CLASS');  //['KH E CLASS' , function ] 
  // 관례 : let [title, setTitle] <= set을 붙이는 것이 관례다.
  /*
    비구조화 할당 문법 : 배열이나 객체에 들어가 있는 값을 "쉽게" 변수로 할당하는 문법

    let arr = {1,2};
    let a   =[0];
    ley b   =[1];

    let [a,b] = arr;

    useState()함수의 반환값은 길이 2짜리 배열[]이며, 배열 비구조화 할당 문법을 통해 쉽게 배열에 있는 값을
    변수로 할당한 것,
    반환된 배열의 0번 인덱스에 있는 값을 제목2 변수에, 1번 인덱스에 있는 값을 제목변경 함수에 각각 할당한 것.

    인스타 좋아요 방식, 클라이언트가 눌렀을 때 즉시 효과가 나는 것에 쓰이면 좋다.

  */

    let [게시글배열, 게시글배열변경함수] = useState(data);


    function 제목2변경 () {
      // 제목2 = "KH C CLASS"; // 단순 대입연산자를 활용하는 경우 state값의 변경점을 reactDom이 알지 못함
      제목변경함수("KH C CLASS"); // useState의 두 번째 매개변수로 전달받은 함수를 통해 변경시 화면이 재렌더링한다.
    }


    const 게시글삭제 = (삭제할글번호) =>{
    //        //1) 배열에서 삭제를 담당하는 메서드 Array.splice(인덱스위치, 삭제할 숫자)
    //        for(let i = 0; i<게시글배열.length; i++) {
    //         if(게시글배열[i].글번호 == 삭제할글번호){
    //           게시글배열.splice(i,1);
    //           break;
    //         }
    //     }
    //       // ...전개연산자[a,b,c] -> a,b,c  // ...{key : value, key : value} -> key : value, key : value
    //        게시글배열변경함수([...게시글배열]); // 값이 제거된 배열을 넣어줬음에도 렌더링이 이루어지지 않는다.
    //        // 주솟값 자체는 변하지 않아서 렌더링이 이루어지지 않음.
    // }

            //2) 게시글배열에서 글번호와 일치하지 않는 게시글만 필터링하기. (filter 함수 이용)
            게시글배열변경함수(게시글배열.filter((게시글) => 게시글.글번호 !== 삭제할글번호));

    }
    let [글제목,글제목변경함수] = useState(''); // 초기값으로 빈문자열''
    let [글내용,글내용변경함수] = useState('');
    let [작성자,작성자변경함수] = useState('');

    function 게시글등록 () {
      /*
        게시글 등록 작업순서
        1) 내가 입력한 값 가져오기
        2) 가져온 데이터를 바탕으로 게시글 객체 생성하기
        3) 생성한 게시글 객체를 게시글 배열에 추가한 후, 게시글배열 변경함수 호출하기(렌더링)

        1) 내가 입력한 값 가져오기
        js방식 : document.querySelector(선택자).value
        react 방식 : state(글제목, 글내용, 작성자)
      */
        console.log(글내용,글제목,작성자);

        //2) 가져온 데이터를 바탕으로 게시글 객체 생성하기
        let 게시글 = {
          // 글번호는 게시글바 열에서 고유해야함. 게시글 배열에서 가장 큰 글번호 값을 찾은 후 +1해서 반횐해줄 예정
            글번호:  Math.max(게시글배열.map( function(게시글, 인덱스){return 게시글.글번호})) +1,
            글내용, // key, value값에 들어가는 변수명이 동일한경우 속성명 단축구문을 제공
            작성자,
            작성일,
            작성일 : new Date().toLocaleDateString() // 2024. 01. 15 
        }
        //3) 생성한 게시글 객체를 게시글 배열에 추가한 후, 게시글배열 변경함수 호출하기(렌더링)
        게시글배열.push(게시글);
        게시글배열변경함수([...게시글배열]);
      }

  return (
    <div className="App">
      {/* 
          jsx문법 내부에서의 주석
          jsx문법) 
          js문법 내부에 html 코드를 작성할 수 있는 문법을 jsx문법이라고 한다.
          리액트에서 ui를 구성할 때 보편적으로 사용되는 방법으로 복잡한 코드를 짤 필요 없이,
          동적으로 추가되는 dom 요소를 단순 코드선언으로 생성할 수 있게 도와준다.
      */}
      <div className="header">
            <h3 style={ { fontWeight : "bolder"}  }>{제목2}</h3>
      </div>
      <div className='nav'>
        {/* 
           react방식 event 부여
           onClick 내부에는 무조건 함수가 들어가야 한다.
            => onClick={함수}
            *주의점 : 무조건 함수자료형 값만 넣어줘야함. 함수 호출한 결과값 넣어주면 의미가 없다.
        */}
          <button onClick={제목2변경}>제목 변경 테스트</button>
          <button>게시판</button>
          <button>등록</button>
      </div>
      <div className='outer'>
        <br/>
        <h2>일반게시판</h2>
        <table className='list-table'>
          <thead>
            {/*  번호, 제목, 작성자, 작성일 , 삭제에 각각 
            크기 10%    40%   20%   20%      10% 수정  */}
            <tr>
              <th style={ {width : "10%" }}>번호</th>
              <th style={ {width : "40%" }}>제목</th>
              <th style={ {width : "20%" }}>작성자</th>
              <th style={ {width : "20%" }}>작성일</th>
              <th style={ {width : "10%" }}>삭제</th>
            </tr>
          </thead>
          <tbody>
            {/* 
               Array내부의 map함수를 사용 예정
            */

                게시글배열.map(function( 게시글, 인덱스) {

                  return (
                  <tr key={인덱스}>
                    <td>{게시글.글번호}</td>
                    <td>{게시글.글제목}</td>
                    <td>{게시글.작성자}</td>
                    <td>{게시글.작성일}</td>
                    {/* 
                        게시글 삭제 기능
                        1. 삭제하고자 하는 게시글을 특정할 수 있어야 한다.
                        2. 게시글 배열에서 삭제하고자하는 게시글을 없애준다.
                        3. 내가 선택한 게시글이 제거된 게시글 배열로 화면이 렌더링 되어야 한다.
                    */}
                    <td><button onClick={() => {게시글삭제(게시글.글번호)}}>삭제</button></td>
                  </tr>
                  )
                } )
              } 
          </tbody>
        </table>
      </div>

      <div className='outer'>
              <br/>
              <h2>게시판등록</h2>
              <table className='enroll-table'>
                <tr>
                  <th>제목</th>
                  <td colSpan={3}>
                    <input type='text' name='글제목'
                     onChange={function(e){
                        글제목변경함수(e.target.value)
                     }}
                    />
                  </td>
                </tr>
                <tr>
                  <th>작성자</th>
                  <td colSpan={3}>
                    <input type='text' name='작성자' 
                    onChange={function(e){
                        작성자변경함수(e.target.value) 
                     }}
                     />
                  </td>
                </tr>
                <tr>
                  <th>글내용</th>
                  <td colSpan={3} style={ {height : "200px"} }>
                    <textarea name='글내용'>
                    onChange={function(e){
                        글내용변경함수(e.target.value) 
                     }}
                     </textarea>
                  </td>
                </tr>
                    <th colSpan={4}><button onClick={게시글등록}>등록</button></th>
              </table>

      </div>

    </div>
  );
}

export default App;
